INSERT INTO `categories` VALUES ('100096', 'Về chúng tôi', null, '1', null, '1485464440', '1', '1530209655', '1', '', '', '', null, '1', '', '[{\"type\":\"content\",\"title\":\"\",\"description\":\"\",\"content\":\"<div class=\\\"container company\\\">\\n<h1 class=\\\"text-center\\\">OIBI JSC</h1>\\n\\n<h2 class=\\\"text-center\\\">Dedicated and Devoted</h2>\\n\\n<hr /></div>\\n\\n<div class=\\\"container information\\\">\\n<h2>GENERAL INFORMATION</h2>\\n\\n<p style=\\\"font-weight: 500\\\">Company name: IBI CONSTRUCTION COMPANY LIMITED</p>\\n\\n<p style=\\\"font-weight: 500\\\">Abbreviation: IBI CONSTRUCTION CO., LTD</p>\\n\\n<p style=\\\"font-weight: 500\\\">Tax code: 0107991843</p>\\n\\n<p style=\\\"font-weight: 500\\\">Contact information:</p>\\n\\n<ul>\\n\\t<li>Office: No 32, lot 06, zone 4.1CC, Lang Ha - Thanh Xuan Street, Nhan Chinh ward, Thanh Xuan District, Ha Noi</li>\\n\\t<li>Tel: +84-24-6652 3588</li>\\n\\t<li>Fax: +84-24-6652 3588</li>\\n\\t<li>Email: ibicon.info@gmail.com</li>\\n</ul>\\n</div>\\n\\n<div class=\\\"container history\\\">\\n<h2>COMPANY HISTORY</h2>\\n\\n<p>IBI Construction Co., Ltd formerly known as Vietbuild Joint Stock Company Activities in the field of construction of reinforced concrete structures, technical infrastructure, architectural works, industrial works. Our company has been executing contracting services for famous enterprises like Samsung Electronics, LG Electronics, KR EMS, Bokwang, Glonics, DK Electronics...</p>\\n\\n<p>In the process of working with big partners, We have met the most stringent technical standards in the field of construction for large and huge factory like Samsung Metal factory, LG Innotek Hai Phong factory, Samsung Component No.4 factory. Besides, With a team of engineers trained in a professional working environment, we have created a high quality management style of IBI Construction Co., Ltd.</p>\\n\\n<p>We understand that the rapid progress of construction is a cost savings for both Employee and IBI c